// src/advanced/hooks/useCart.ts - localStorage 동기화가 Hook 내부에
useEffect(() => {
  if (cart.length > 0) {
    saveDataToStorage("cart", cart);
  } else {
    removeDataFromStorage("cart");
  }
}, [cart]);
