import { CartItem, Coupon } from '../../../types';

// 장바구니 총액 계산 결과 타입
export interface CartTotal {
  totalBeforeDiscount: number;  // 할인 전 총액
  totalAfterDiscount: number;   // 할인 후 총액
}

// 장바구니 계산에 필요한 옵션들
