    expect(fetchHolidays(new Date('2025-07-01'))).toEqual({});
  });

  it('여러 공휴일이 있는 10월에 대해 모든 공휴일을 반환한다', () => {
    expect(fetchHolidays(new Date('2025-10-05'))).toEqual({
      '2025-10-03': '개천절',
      '2025-10-05': '추석',
      '2025-10-06': '추석',
      '2025-10-07': '추석',
      '2025-10-09': '한글날',
    });
  });
});
```
